const initialState = {
    items: [],
    isLoading: false
}

const weather = (state = initialState, action) => {
    switch(action.type){
        case 'SET_DATA_WEATHER':
            return {
                ...state,
                items: [...action.payload],
                isLoading: false
            }
        case 'SET_LOADING':
            return {
                ...state,
                isLoading: action.payload
            }
        default:
            return state
    }
}

export default weather
