const initialState = {
    items: [],
    isLoading: false
}


const findDay = (day, dayWeek) => {
    let date = new Date(day.dt_txt).toLocaleString("ru", {weekday: "long"});
    return date === dayWeek
}

const week = (arr) => {
    const result =  arr.reduce((arr, dayWeek) => {
        let weekday = new Date(dayWeek.dt_txt).toLocaleString("ru", {weekday: "long", month: 'long', day: 'numeric'});
        // const [week, month] =  weekday.split(',')
        if(!arr.includes(weekday)){
            return [...arr, weekday]
        }
        return arr
    }, [])
    console.log(result)
    return result
}


const createArr = (arr) => {
    return(
        arr.map(( elem => {
            let dateVal = new Date( elem.dt_txt ).toLocaleString("ru", {
            month: "long", 
            day: "numeric", 
            hour: "numeric",
            minute: "numeric" 
            });
            const [date, time] = dateVal.split(',')
            return {
                date,
                time,
                temp: Math.ceil(elem.main.temp), 
                feels_like: Math.ceil(elem.main.feels_like),
                weather: elem.weather[0]
                }
            } )
        )
    )
}

const weather = (state = initialState, action) => {
    switch(action.type){
        case 'SET_DATA_WEATHER':

            const newItems = week(action.payload.list).map((item, i, arr) => {
                return {
                    day: item,
                    data: createArr( action.payload.list.filter(day => findDay(day, item)))
                }
            })

            return {
                ...state,
                items: newItems,
                isLoading: false
            }
        case 'SET_LOADING':
            return {
                ...state,
                isLoading: action.payload
            }
        default:
            return state
    }
}

export default weather
