const initialState = {
    items: [],
    isLoading: false
}

const weather = (state = initialState, action) => {
    switch(action.type){
        case 'SET_DATA_WEATHER':

        console.log(action.payload)

        const newObj = {
            id: 1,
            city: action.payload.city.name
        }
            return {
                ...state,
                items: newObj,
                isLoading: false
            }
        case 'SET_LOADING':
            return {
                ...state,
                isLoading: action.payload
            }
        default:
            return state
    }
}

export default weather
