import React from "react"
import { useDispatch, useSelector } from 'react-redux'

import 'swiper/swiper-bundle.css';

import { getDataWeather } from "../redux/actions/weather";
import { ForecastWeather } from "../components";


const Home = () => {

    const {items, isLoading  } = useSelector(({weather}) => weather)
    const dispatch = useDispatch()


    // React.useEffect(() => {
    //     axios.get('https://api.openweathermap.org/data/2.5/forecast?q=Москва&units=metric&lang=ru&appid=9564702c3543cd18a286664bcd09dcac').then(resp => {
    //         console.log(resp.data)
    //     })
    // }, [])

    React.useEffect(() => {
        dispatch(getDataWeather())
    }, [])

    console.log(items)

    return (
        <div className='main'>
            <div className='container'>
                
                <div className='title'>Москва</div>
                {
                    items.map((item, _, arr) =>
                        <ForecastWeather 
                            key={item.day}
                            today={arr[0].day}
                            nextday={arr[1].day}
                            date={item.date}
                            day={item.day}
                            data={item.data} /> )
                }
            </div>
        </div>
    )
}

export default Home
