import * as axios from 'axios';

const instance = axios.create({
    baseURL:"https://api.openweathermap.org/data/2.5/"
})

export const weatherApi = {
    byCoord(lat,lon){
        return instance.get(`weather?lat=${lat}&lon=${lon}&units=metric&lang=ru&appid=9564702c3543cd18a286664bcd09dcac`)
    },
    
    byCityName(city){
        return instance.get(`weather?q=${city}&units=metric&lang=ru&appid=9564702c3543cd18a286664bcd09dcac`)
    },

    forWeek(city){
        return instance.get(`forecast?q=${city}&lang=ru&units=metric&appid=9564702c3543cd18a286664bcd09dcac`)
        .then( res => res.data.list ) 
    }
}

