import React from 'react'
import { Swiper, SwiperSlide } from 'swiper/react';

import cloudyNightSvg from '../assets/cloudy-night.svg'



const ForecastWeather = ({day, today, month, nextday, data}) => {
    console.log(month)

    return (
        <div className='card'>
            <div className='card__title'>{day === today ? 'Сегодня' : day === nextday ? 'Завтра' : `${month}, ${day}`}</div>
            <div className='card__items'>
                <Swiper 
                    spaceBetween = {50} 
                    slidesPerView = {1}
                    initialSlide = {4}
                    breakpoints = {{
                        600 : {slidesPerView:4}
                    }}
                >
                    {
                        data.map(elem =>  <SwiperSlide key={elem.time}>
                            <div className='weather'>
                                <div className='time'>{`${month},${elem.time}`}</div>
                                <div className='info'>
                                    <div>
                                        <img src={cloudyNightSvg} alt="" />
                                    </div>
                                    <div className='temp'>{elem.temp}°C</div>
                                </div>
                                <div>
                                    <p className='description'>{elem.weather.description}</p>
                                    <span className='description'>Ощущается как: {elem.feels_like}°C</span>
                                </div>
                            </div>
                        </SwiperSlide>)
                    }
                </Swiper>
            </div>
        </div>
    )
}

export default ForecastWeather
