import React from 'react'
import { Swiper, SwiperSlide } from 'swiper/react';

import cloudyNightSvg from '../assets/cloudy-night.svg'



const ForecastWeather = ({day  }) => {

    return (
        <div className='card'>
            <div className='card__title'>{day}</div>
            <div className='card__items'>
                <Swiper 
                    spaceBetween = {50} 
                    slidesPerView = {1}
                    initialSlide = {4}
                    breakpoints = {{
                        600 : {slidesPerView:4}
                    }}
                >
                    <SwiperSlide>
                        <div className='weather'>
                            <div className='time'>21 июня, 12:00</div>
                            <div className='info'>
                                <div>
                                    <img src={cloudyNightSvg} alt="" />
                                </div>
                                <div className='temp'>29°C</div>
                            </div>
                            <div>
                                <p className='description'>Пасмурно</p>
                                <span className='description'>Ощущается как: 28</span>
                            </div>
                        </div>
                    </SwiperSlide>
                </Swiper>
            </div>
        </div>
    )
}

export default ForecastWeather
