import {weatherUrl} from '../../api/api'

const setDataWeather = (city, items) => ({ type: 'SET_DATA_WEATHER', payload: {city, items}})

export const getDataWeather = () => async (dispatch) => {
    const resp = await weatherUrl.getWeather()
    console.log(resp.data.list)
    console.log(resp.data.city.name)
    dispatch(setDataWeather(resp.data.city.name, resp.data.list))
}
