const initialState = {
    items: [],
    isLoading: false
}
const findDay = (day, dayWeek) => {
    let date = new Date(day.dt_txt).toLocaleString("ru", {weekday: "long"} );
    // console.log(date)
    return date === dayWeek
}


const createArr = (arr) => {
    console.log(arr)
}

const weather = (state = initialState, action) => {
    switch(action.type){
        case 'SET_DATA_WEATHER':

            let week = [
                { day: 'понедельник', data: createArr( action.payload.list.filter( (day) => findDay( day, 'понедельник') ) ) },
                { day: 'вторник', data: createArr( action.payload.list.filter( (day) => findDay( day, 'вторник') ) ) },
                { day: 'среда', data: createArr( action.payload.list.filter( (day) => findDay( day, 'среда') ) ) },
                { day: 'четверг', data: createArr( action.payload.list.filter( (day) => findDay( day, 'четверг') ) ) },
                { day: 'пятница', data: createArr( action.payload.list.filter( (day) => findDay( day, 'пятница') ) ) },
                { day: 'суббота', data: createArr( action.payload.list.filter( (day) => findDay( day, 'суббота') ) ) },
                { day: 'воскресенье', data: createArr( action.payload.list.filter( (day) => findDay( day, 'воскресенье') ) ) },
            ]

        // console.log(week)

        const newObj = {
            id: 1,
            city: action.payload.city.name
        }
            return {
                ...state,
                items: newObj,
                isLoading: false
            }
        case 'SET_LOADING':
            return {
                ...state,
                isLoading: action.payload
            }
        default:
            return state
    }
}

export default weather
